import React, {useState} from 'react';
import "./SignIn.css";
import {Link,useHistory} from "react-router-dom";
import {auth} from "../../../firebase/firebase";
const SignIn = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const history = useHistory();

    const handleSignIn = (e) => {
        e.preventDefault();
        auth.signInWithEmailAndPassword(email, password)
            .then((user) => {
                if(user) history.push("/");
            })
            .catch(err => alert(err.message));
    }

    const handleRegister = (e) => {
        e.preventDefault();
        auth.createUserWithEmailAndPassword(email, password)
            .then(auth => {
                if(auth) history.push("/");
            })
            .catch(err => alert(err.message));
    }

    return (
        <div className="page sign-page">
            <Link to="/" className="amazon-logo">
                <img src="data:image/png;base64," alt="logo"/>
            </Link>
            <div className="sign-in-container">
                <h1>Sign-In</h1>
                <form className="sign-form">
                    <div className="form-input">
                        <label htmlFor="email">Email</label>
                        <input
                            id="email"
                            type="text"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}/>
                    </div>
                    <div className="form-input">
                        <label htmlFor="password">Password</label>
                        <input
                            id="password"
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}/>
                    </div>
                    <button
                        onClick={handleSignIn}
                        className="button sign-in-button">Sign In</button>
                    <p>
                        By continuing, you agree to Amazon's Conditions of Use and Privacy Notice.
                    </p>
                </form>

            </div>
            <div className="new-account">
                <div className="new-account-hr">
                    <hr width="110"/> New to Amazon? <hr width="110"/>
                </div>
                <button
                    onClick={handleRegister}
                    type="submit"
                    className="button new-account-button">Create your Amazon account</button>
            </div>
        </div>
    );
}

export default SignIn;